import org.bukkit.ChatColor;
import org.bukkit.entity.EntityType;
import org.bukkit.entity.Player;
import org.json.simple.JSONArray;
import org.json.simple.JSONObject;
import org.json.simple.parser.JSONParser;

import java.io.IOException;
import java.io.InputStreamReader;
import java.net.URL;
import java.net.URLConnection;
import java.util.ArrayList;
import java.util.Collection;
import java.util.List;
import java.util.UUID;
import java.util.concurrent.TimeUnit;
import java.util.logging.Level;

/**
 * Some Skin utils.
 */
public class Skins {
    public static final List<Skin> DOT_SKINS;
    public static final ArrayListMultimap<EntityType,Skin> MOB_SKINS;
    public static final Skin DEFAULT_SKIN;

    public static final Skin DECOR_TV;
    public static final Skin DECOR_RADIO;
    public static final Skin DECOR_SKULL;
    public static final Skin DECOR_AQUARIUM;
    public static final Skin DECOR_CLOCK;
    public static final Skin DECOR_PRESENT;
    public static final Skin DECOR_DOUGHNUT;

    public static final Skin BLOCK_COBBLE;
    public static final Skin BLOCK_LOG;

    private static final String profileUrl = "https://sessionserver.mojang.com/session/minecraft/profile/";
    private static final LoadingCache<String, String> profileCache;

    static {
        DEFAULT_SKIN = new Skin("", "");

        DECOR_TV = new Skin("", "");
        DECOR_RADIO = new Skin("", "");
        DECOR_SKULL = new Skin("", "");
        DECOR_AQUARIUM = new Skin("", "");
        DECOR_CLOCK = new Skin("", "");
        DECOR_PRESENT = new Skin("", "");
        DECOR_DOUGHNUT = new Skin("", "");

        BLOCK_COBBLE = new Skin("", "");
        BLOCK_LOG = new Skin("", "");

        DOT_SKINS = new ArrayList<>();
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));
        DOT_SKINS.add(new Skin("", ""));

        MOB_SKINS = ArrayListMultimap.create();
        MOB_SKINS.put(EntityType.PIG, new Skin("", ""));
        MOB_SKINS.put(EntityType.COW, new Skin("", ""));
        MOB_SKINS.put(EntityType.CHICKEN, new Skin("", ""));
        MOB_SKINS.put(EntityType.WOLF, new Skin("", ""));
        MOB_SKINS.put(EntityType.OCELOT, new Skin("", ""));
        MOB_SKINS.put(EntityType.SHEEP, new Skin("", ""));
        MOB_SKINS.put(EntityType.SQUID, new Skin("", ""));
        MOB_SKINS.put(EntityType.VILLAGER, new Skin("", ""));
        MOB_SKINS.put(EntityType.SLIME, new Skin("", ""));
        MOB_SKINS.put(EntityType.MAGMA_CUBE, new Skin("", ""));
        MOB_SKINS.put(EntityType.CREEPER, new Skin("", ""));
        MOB_SKINS.put(EntityType.ZOMBIE, new Skin("", ""));
        MOB_SKINS.put(EntityType.SKELETON, new Skin("", ""));
        MOB_SKINS.put(EntityType.SKELETON, new Skin("", ""));
        MOB_SKINS.put(EntityType.WITHER, new Skin("", ""));
        MOB_SKINS.put(EntityType.ENDERMAN, new Skin("", ""));
        MOB_SKINS.put(EntityType.IRON_GOLEM, new Skin("", ""));
        MOB_SKINS.put(EntityType.PIG_ZOMBIE, new Skin("", ""));
        MOB_SKINS.put(EntityType.BLAZE, new Skin("", ""));
        MOB_SKINS.put(EntityType.GHAST, new Skin("", ""));
        MOB_SKINS.put(EntityType.GUARDIAN, new Skin("", ""));
        MOB_SKINS.put(EntityType.GUARDIAN, new Skin("", ""));
        MOB_SKINS.put(EntityType.SNOWMAN, new Skin("", ""));

        profileCache = CacheBuilder.newBuilder().maximumSize(500).expireAfterWrite(4, TimeUnit.HOURS).build(new CacheLoader<String, String>() {
            @Override
            public String load(String uuid) throws IOException {
                return getProfileText(uuid);
            }
        });
    }

    /**
     * Get a skin from an entity type.
     * @param type
     * @return
     */
    public static Skin getMob(EntityType type) {
        return getMob(type, 0);
    }

    /**
     * Get a skin from an entity type and alternate form number.
     * @param type
     * @param alternateForm Used if multiple versions of this mob exist (wither skeleton = 1, ender guardian = 1)
     * @return
     */
    public static Skin getMob(EntityType type, int alternateForm) {
        List<Skin> skins = MOB_SKINS.get(type);
        if (skins == null || skins.size() == 0)
            return DEFAULT_SKIN;
        if (alternateForm >= skins.size())
            return DEFAULT_SKIN;
        return skins.get(alternateForm);
    }

    /**
     * Get a cool skin based on a chat color (grey square with a colored dot).
     * @param color
     * @return
     */
    public static Skin getDot(ChatColor color) {
        return DOT_SKINS.get(color.ordinal());
    }

    /**
     * Get a skin from an online player.
     * @param player
     * @return
     */
    public static Skin getPlayer(Player player) {
        WrappedSignedProperty property = DEFAULT_SKIN.getProperty();
        Collection<WrappedSignedProperty> properties = WrappedGameProfile.fromPlayer(player).getProperties().get("textures");
        if (properties != null && properties.size() > 0)
            property = properties.iterator().next();
        return new Skin(property);
    }

    /**
     * Get a Minecraft user's skin.
     * @param username
     * @return
     */
    public static Skin getPlayer(String username) {
        if (!Bukkit.getOnlineMode()) {
            Tabbed.log(Level.SEVERE, "online-mode must be true to fetch skins");
            return DEFAULT_SKIN;
        }
        return getPlayer(Bukkit.getOfflinePlayer(username).getUniqueId());
    }

    /**
     * Get a Minecraft user's skin.
     * @param uuid
     * @return
     */
    public static Skin getPlayer(UUID uuid) {
        try {
            return downloadSkin(uuid.toString().replace("-", ""));
        } catch (Exception e) {
            e.printStackTrace();
            return DEFAULT_SKIN;
        }
    }

    private static Skin downloadSkin(String uuid) {
        uuid = addUuidDashes(uuid);

        WrappedSignedProperty property = null;

        JSONObject json;
        try {
            json = (JSONObject) new JSONParser().parse(profileCache.get(uuid));
        } catch (Exception e) {
            Tabbed.log(Level.SEVERE, "Unable to fetch skin for uuid " + uuid);
            e.printStackTrace();
            return DEFAULT_SKIN;
        }
        JSONArray properties = (JSONArray) json.get("properties");

        for (Object object : properties) {
            JSONObject jsonObject = (JSONObject) object;
            String name = (String) jsonObject.get("name");
            String value = (String) jsonObject.get("value");
            String signature = (String) jsonObject.get("signature");
            if (name.equals("textures"))
                property = new WrappedSignedProperty(name, value, signature);
        }

        if (property == null)
            return DEFAULT_SKIN;

        return new Skin(property);
    }

    private static String addUuidDashes(String uuid) {
        return uuid.replaceAll("(\\w{8})(\\w{4})(\\w{4})(\\w{4})(\\w{12})", "$1-$2-$3-$4-$5");
    }

    private static String getProfileText(String uuid) throws IOException {
        uuid = uuid.replace("-", "");

        URL url = new URL(profileUrl + uuid + "?unsigned=false");
        URLConnection connection = url.openConnection();
        return CharStreams.toString(new InputStreamReader(connection.getInputStream(), Charsets.UTF_8));
    }
}
